nums.forEach((value) => numSet.add(value));

console.log(numSet);  // Set { 1, 2, 3 }


// Iterating through and using includes()
const singleValueArr = [];

nums.forEach((value) => {
  if (!singleValueArr.includes(value)) {
    singleValueArr.push(value);
  }
});

console.log(singleValueArr);  // [ 1, 2, 3 ]

