Great detail exists there, but the code is:

// Best 2-Pointer solution of 3-Sum I could arrive at
var threeSum3 = function (nums) {
  nums.sort((a, b) => a - b);

  const result = [];
  for (let indexA = 0; indexA < nums.length - 2; indexA++) {if (window.CP.shouldStopExecution(0)) break;
    const a = nums[indexA];

    if (a > 0) return result;
    if (a === nums[indexA - 1]) continue;

    let indexB = indexA + 1;
    let indexC = nums.length - 1;

    // Now check if sum is zero, and if NOT, then run the next set of 2 if loop to update indexB and indexC
    while (indexB < indexC) {if (window.CP.shouldStopExecution(1)) break;
      const b = nums[indexB];
      const c = nums[indexC];

      if (a + b + c === 0) {
        result.push([a, b, c]);
      }

      // Now with the below 2 if functions, I am just implementing how the indexB and indexC will be incremented and decremented with each iteration and gets feeded back to the above while function ( while (indexB < indexC ))

      if (a + b + c >= 0) {
        while (nums[indexC - 1] === c) {if (window.CP.shouldStopExecution(2)) break;indexC--;} // This is equivalent to continue in my previous implementation
        window.CP.exitedLoop(2);indexC--;
      }

      if (a + b + c <= 0) {
        while (nums[indexB + 1] === b) {if (window.CP.shouldStopExecution(3)) break;indexB++;} // This is equivalent to continue in my previous implementation
        window.CP.exitedLoop(3);indexB++;
      }
    }window.CP.exitedLoop(1);
  }window.CP.exitedLoop(0);
  return result;
};

