/**
 * @param {string[]} words
 * @param {string} order
 * @return {boolean}
 */
var isAlienSorted = function(words, order) {
  const compareWord1AndWord2 = (word1, word2) => {
    let i = 0;
    while (word1[i] || word2[i]) {
      if (!word1[i]) {  // only word2 is left, indicating that word1 is small and returns true
        return true
      }
      if (!word2[i]) {  // only word1 is left, indicating that word2 is small and returns false
        return false    
      }
      // Both exist
      if (order.indexOf(word1[i]) === order.indexOf(word2[i])) {  // jump to the next character if the characters are equal
        i++;
        continue;
      } else {
        return order.indexOf(word1[i]) < order.indexOf(word2[i])  // The current word order is larger
      }
    }
    return true // returns true when all characters are equal
  }

  for (let index = 0; index < words.length ; index++) {
    if (!compareWord1AndWord2(words[index-1] || '', words[index])) {
      return false
    }
  }
  return true
};

