var maxProfit = function(prices) {
    let i = 0, j = 1;
    let profits = 0;
    for (; j < prices.length; ) {
        if (prices[j] - prices[i] > 0) {
            profits += prices[j] - prices[i];
            j++;
            i++;
        } else {
            i++;
            j++;
        }
    }
    return profits;
};

