function Add(number1, number2)
{
  while (number2 != 0)
  {
    var carry = number1 & number2;

    // 0000000000001111 &
    // 0000000000100000
    // 0000000000000000

    number1 = number1 ^ number2;
    // 0000000000001111 ^
    // 0000000000100000
    // 0000000000101111 = 47

    number2 = carry << 1;
  }

  return number1;
}

