class Graph
{
  constructor(numberOfVertices)
  {
    this.numberOfVertices = numberOfVertices;
    this.AdjacentList = new Map();
  }

  addVertex(vertex)
  {
    this.AdjacentList.set(vertex, []);
  }

  addEdge(vertex, edge)
  {
    this.AdjacentList.get(vertex).push(edge);
    this.AdjacentList.get(edge).push(vertex);
  }

  printGraph()
  {
    var getVertices = this.AdjacentList.keys();

    for (var v of getVertices)
    {
      var getValues = this.AdjacentList.get(v);

      var concatenated = "";
      for (var value of getValues)
      {
        concatenated += value + " ";
      }

      console.log(v + " has neighbors " + concatenated);
    }
  }
}

