function findFactors(n)
{
  var result = [];

  findResult(n, 2, [], result);

  return result;
}

function findResult(n, startingFactor, currentResult, finalResult)
{
  if (n === 1)
  {
    if (currentResult.length > 1)
    {
      finalResult.push(currentResult.slice());
    }

    return;
  }

  for (var i = startingFactor; i <= n; i++)
  {
    if (n % i === 0)
    {
      currentResult.push(i);

      findResult(n / i, i, currentResult, finalResult);

      currentResult.pop();
    }
  }
}

console.log(findFactors(16));

