This answer comes from:
https://beizhedenglong.github.io/leetcode-solutions/docs/construct-binary-tree-from-preorder-and-inorder-traversal


/**
 * Definition for a binary tree node.
 * function TreeNode(val) {
 *     this.val = val;
 *     this.left = this.right = null;
 * }
 */
/**
 * @param {number[]} preorder
 * @param {number[]} inorder
 * @return {TreeNode}
 */
const buildTree = (preorder, inorder) => {
  if (preorder.length === 0) {
    return null
  }

  const node = {
    val: preorder[0],
    left: null,
    right: null,
  }
  const index = inorder.findIndex(v => v === preorder[0])
  node.left = buildTree(preorder.slice(1, 1 + index), inorder.slice(0, index))
  node.right = buildTree(preorder.slice(1 + index), inorder.slice(index + 1))
  return node
}

