This answer comes to us from:
https://leetcode.com/problems/copy-list-with-random-pointer/discuss/735195/Javascript-All-three-solutions-with-comments-Easy-to-understand


/**
 * // Definition for a Node.
 * function Node(val, next, random) {
 *    this.val = val;
 *    this.next = next;
 *    this.random = random;
 * };
 */

/**
 * @param {Node} head
 * @return {Node}
 */
var copyRandomList = function(head) {
    // Recursive TC - O(n), SC - O(n)
    let map = new Map();

    function copy(node) {
        if (node == null) {
            return null;
        }
        if (map.get(node) != null) {
            return map.get(node);
        }
        const n = new Node(node.val);
        map.set(node, n);
        console.log(map);
        n.next = copy(node.next);
        n.random = copy(node.random);
        return n;
    };

    return copy(head);
    // Iterative TC - O(n), SC - O(n)
    let list = new Node(0);
    let temp = list;
    let curr = head;
    let visited = new Map();
    // first create next pointers
    while (curr !== null) {
        let node = new Node(curr.val);
        visited.set(curr, node);
        temp.next = node;
        temp = temp.next;
        curr = curr.next;
    }
    temp = list.next;
    curr = head;
    // create random pointers
    while (curr !== null) {
        if (curr.random) {
            temp.random = visited.get(curr.random);
        }
        temp = temp.next;
        curr = curr.next;
    }
    return list.next;
    // Iterative O(1) space, O(n) TC
    if (head == null) {
        return null;
    }
    let temp = head;
    // first set the next pointers
    while (temp !== null) {
        let newNode = new Node(temp.val);
        newNode.next = temp.next;
        temp.next = newNode;
        temp = newNode.next;
    }
    // set the random pointers now
    temp = head;
    while (temp !== null) {
        temp.next.random = temp.random ? temp.random.next : null;
        temp = temp.next.next;
    }
    // unweave the list now
    let oldList = head;
    let newList = head.next;
    let dummy = newList;
    while (oldList !== null) {
        oldList.next = oldList.next.next;
        newList.next = newList.next ? newList.next.next : null;
        oldList = oldList.next;
        newList = newList.next;
    }
    return dummy;
};

