var countPrimes = function (number) {
  const numbers = new Array(number + 1);
  numbers.fill(true);
  numbers[0] = numbers[1] = false;

  let i = 2
  while (i <= Math.sqrt(number)) {
    for (let j = 2; i * j <= number; j++) numbers[i * j] = false;
    i++
  }

