const findPalindrome = number => {

  if (number < 0)
  {
    return false;
  }

  let reversed = 0;

  let copy = number;

  while (copy > 0)
  {
    const lastDigit = copy % 10;

    reversed = (reversed * 10) + lastDigit;

    const toDecimal = copy / 10;

    const lastDecimalRemoved = toDecimal | 0;

    copy = lastDecimalRemoved;
  }

  return number === reversed;
}

