https://codebasil.com/problems/the-celebrity-problem

/**
 * @param {number} n
 * @return {number} Index of the celebrity, or -1.
 */
function theCelebrityProblem(n) {
  // Handles edge cases.
  if (n <= 0) {
    return -1;
  }

  // We treat everyone as a candidate at first.
  let candidates = [];
  for (let i = 0; i < n; i++) candidates.push(i);

  // At each iteration, filters out half the candidates.
  // Repeat until there's 1 left.
  while (candidates.length > 1) {
    let nextCandidates = [];

    // Pairs up 2 adjacent people.
    for (let i = 0; i < candidates.length; i += 2) {
      // The last candidate in an odd-length array won't be paired,
      // so we add them to the next batch.
      if (i == candidates.length - 1) {
        nextCandidates.push(candidates[i]);
      }
      // i knows i+1, so i cannot be celebrity.
      else if (knows(candidates[i], candidates[i + 1])) {
        nextCandidates.push(candidates[i + 1]);
      } 
      // i doesnt know i+1, so i+1 cannot be a celebrity.
      else {
        nextCandidates.push(candidates[i]);
      }
    }

    candidates = nextCandidates;
  }

  // Verifies that this candidate does not know anyone.
  const celebrity = candidates[0];

  for (let i = 0; i < n; i++) {
    if (i != celebrity && knows(celebrity, i)) {
      return -1;
    }
  }

  return celebrity;
}

