function countNumberOfOneBits(number)
{
  var result;

  everyOtherBitRemoved = number & 0x55555555; 

  removedBitsLinedUp = (number >> 1) & 0x55555555;

  sumOfEachPairOfBits = everyOtherBitRemoved + removedBitsLinedUp;
  result = sumOfEachPairOfBits;

  zeroEveryOtherPair = result & 0x33333333;

  shiftedRemainingPairs = (result >> 2) & 0x33333333;

  result = zeroEveryOtherPair + shiftedRemainingPairs;

  result = (result & 0x0F0F0F0F) + ((result >> 4) & 0x0F0F0F0F);

  result = (result & 0x00FF00FF) + ((result >> 8) & 0x00FF00FF);

  result = (result & 0x0000FFFF)  + ((result >> 16) & 0x0000FFFF);

  return result;
}

