function getEditDistance(string1, string2)
{
  if (string1.length == 0)
  {
    return string2.length;
  }
  if (string2.length == 0)
  {
    return string1.length;
  }

  var matrix = [];

  for (row = 0; row <= string2.length; row++)
  {
    matrix[row] = [row];
  }

  for (col = 0; col <= string1.length; col++)
  {
    matrix[0][col] = col;
  }

  for (row = 1; row <= string2.length; row++)
  {
    for (col = 1; col <= string1.length; col++)
    {
      if (string2.charAt(row - 1) == string1.charAt(col - 1))
      {
        matrix[row][col] = matrix[row - 1][col - 1];
      }
      else
      {
        previousRowAndCol = matrix[row - 1][col - 1] + 1;
        previousCol = matrix[row][col - 1];
        previousRow = matrix[row - 1][col];
        previousRowOrCol = Math.min(previousCol + 1, previousRow + 1);

        matrix[row][col] = Math.min(previousRowAndCol, previousRowOrCol);

      }
    }
  }

  return matrix[string2.length][string1.length];
}

