https://dev.to/chakrihacker/implement-trie-prefix-tree-leetcode-5hi


var trieNode = function() {
    this.children = {}
    this.end = false
}

var Trie = function() {
    this.root = new trieNode()
};

Trie.prototype.insert = function(word) {
    let root = this.root
    for(letter of word) {
        if(root.children[letter]) {
            root = root.children[letter]
        } else {
            root.children[letter] = new trieNode()
            root = root.children[letter]
        }
    }
    root.end = true
};

Trie.prototype.search = function(word) {
    let root = this.root
    for(letter of word) {
        if(root.children[letter]) {
            root = root.children[letter]
        } else {
            return false
        }
    }
    return root.end
};

Trie.prototype.startsWith = function(prefix) {
    let root = this.root
    for(letter of prefix) {
        if(root.children[letter]) {
            root = root.children[letter]
        } else {
            return false
        }
    }
    return true
};

