var canJump = function(nums) {
  let max = 0; // Array subscript that can be reached

  for(let i = 0; i < nums.length; i++) {
      if (max < i) return false; // You can’t go this step now, and you can’t go further
      max = Math.max(nums[i] + i, max);
  }

  return max >= nums.length - 1
};

