var kthSmallest = function(root, k) {

    if(root== null) return null;

    let mini = 0;
    let stack = [];
    let temp ;
    while(true){
        if(root!== null){
            stack.push(root);
            root = root.left;
        }else{
            mini += 1;
            if(mini === k && stack.length !== 0) return stack[stack.length-1].val;
            else temp = stack.pop();
            root = temp.right; 
        }
    }

};

