THis answer comes from Mike Mai at:
https://maideveloper.com/blog/largest-rectangle-in-histogram-in-javascript#:~:text=Given%20n%20non%2Dnegative%20integers,6%2C2%2C3%5D.



/**
 * Get the largest rectangle area
 * @param {number[]} heights
 * @return {number}
 * @time complexity: O(n)
 * @space complexity: O(n)
 */
const largestRectangleArea = heights => {

  const n = heights.length,
        stack = [];

  let maxArea = 0;

  for (let i = 0; i < n; i++) {

    // Check if the current bar is lower than the previous bar in the stack
    while (stack.length && heights[i] <= heights[stack[stack.length-1]]) {
      // Calculate the area
      maxArea = Math.max(maxArea, getArea(i, heights, stack));
    }

    stack.push(i);
  }

  // Calcualte the remaining bar in the stack
  while (stack.length) {
    maxArea = Math.max(maxArea, getArea(n, heights, stack));
  }

  return maxArea;
};

/**
 * Calcualte the area
 * @param {number} i          index element in heights
 * @param {number[]} heights
 * @param {number[]} stack
 * @return {number}
 * @time complexity: O(1)
 * @space complexity: O(1)
 */
const getArea = (i, heights, stack) => {
  const h = heights[stack.pop()],
        w = stack.length ? i - stack[stack.length-1] - 1 : i;

  return h * w;
};

