This answer comes from:
https://www.cnblogs.com/liok3187/p/4542262.html


/**
 * @param {number[]} nums
 * @return {number}
 */
var longestConsecutive = function(nums) {
    var set = new Set();
    for(var i = 0; i < nums.length; i++){
        if(!set.has(nums[i])){
            set.add(nums[i]);
        }
    }
    var max = 0;
    var count = 0;
    for(i in nums){
        curr = nums[i];
        if(set.has(nums[i])){
            count = 1;
            set.delete(nums[i]);
        }
        while(set.has(curr - 1)){
            if(set.has(curr - 1)){
                count ++;
                set.delete(curr - 1);
                curr -= 1;
            }
        }
        curr = nums[i];
        while(set.has(curr + 1)){
            if(set.has(curr + 1)){
                count ++;
                set.delete(curr + 1);
                curr += 1;
            }
        }

        if(count > max){
            max = count;
        }
    }

    return max;
};

