/**
 * @param {number[]} nums
 * @param {number} lower
 * @param {number} upper
 * @return {string[]}
 */
// 2 pointers!!!!, prev, curt!
var findMissingRanges = function(nums, lower, upper) {

    // Initialize prev to lower-1 to determine if there is a "first" interval.
    let prev = lower - 1, curt = 0;
    let results = [];

    for (let i = 0; i <= nums.length; i++) {

        // When traversing to length, set curr to upper 1 to determine if there is a "last" interval.
        curt = (i === nums.length) ? upper + 1 : nums[i];

        // If the difference between the previous number and the current number is greater than 1, there is an interval between the two.
        if (curt - prev > 1) {
            results.push(getRange(prev + 1, curt - 1));
        }
        prev = curt;
    }
    return results;
};

function getRange(from, to) {
    return from === to ? JSON.stringify(from) : from + '->' + to;
}

