var partition = function(s) {
    let results = [];
    if(!s || s.length === 0) return results;

    const isPalindrome = str => {
        for(let i = 0, j = str.length - 1; i < j; i++, j--) {
            if(str[i] !== str[j]) return false;
        }
        return true;
    }

    const helper = (s, partition, startIndex, results) => {
        if(startIndex === s.length) { // make sure you are returning the same word -- when index goes all the way to the end, push into results arr 
            results.push(partition.slice());
        }
        for(let i = startIndex; i < s.length; i++) {
            let subStr = s.slice(startIndex, i + 1);
            if(!isPalindrome(subStr)) continue;
            partition.push(subStr);
            helper(s, partition, i + 1, results);
            partition.pop();
        }
    }

    helper(s, [], 0, results)
    return results;
};
