function findLetterCombinations(digitString)
{
  if (digitString.length === 0)
  {
    return [];
  }

  var map = {

    "2": ["A", "B", "C"],
    "3": ["D", "E", "F"],
    "4": ["G", "H", "I"],
    "5": ["J", "K", "L"],
    "6": ["M", "N", "O"],
    "7": ["P", "Q", "R", "S"],
    "8": ["T", "U", "V"],
    "9": ["W", "X", "Y", "Z"]

  };

  var combinations = [''];

  for (var i = 0; i < digitString.length; i++)
  {
    var digit = digitString[i];
    var letters = map[digit];
    var tempArray = [];

    if (letters === undefined)
    {
      continue;
    }

    for (var letterIndex = 0; letterIndex < letters.length; letterIndex++)
    {
      var letterPossible = letters[letterIndex];

      for (var combinationIndex = 0; combinationIndex < combinations.length; combinationIndex++)
      {
        var combination = combinations[combinationIndex];

        tempArray.push(combination + letterPossible);
      }
    }

    combinations = tempArray;
  }

  return combinations;

}

console.log(findLetterCombinations("23"));

