// O(n * n!)

function findPermutations(string)
{
  var result = [];

  if (string.length === 1)
  {
    result.push(string);
    return result;
  }

  for (var i = 0; i < string.length; i++)
  {
    var firstCharacter = string[i];
    var charactersRemaining = string.substring(0, i) + string.substring(i + 1);

    var innerPermutations = findPermutations(charactersRemaining);

    for (character = 0; character < innerPermutations.length; character++)
    {
      result.push(firstCharacter + innerPermutations[character]);
    }
  }

  return result;
}
