    var top = this.dp[row1 - 1] ? this.dp[row1 - 1][col2] : 0;
    var topLeft = this.dp[row1 - 1] && this.dp[row1 - 1][col1 - 1] ? this.dp[row1 - 1][col1 - 1] : 0;
    return this.dp[row2][col2] - left - top + topLeft;
};

