    }

    //left column
    for(var i=0; i<m; i++){
        dp[i][0] = 1;
    }


    //top row
    for(var j=0; j<n; j++){
        dp[0][j] = 1;
    }

    //fill up the dp table
    for(var i=1; i<m; i++){
        for(var j=1; j<n; j++){
            dp[i][j] = dp[i-1][j] + dp[i][j-1];
        }
    }

    return dp[m-1][n-1];
}

