function bubbleSort(array)
{
  let length = array.length;
  let swapped;

  do {
    swapped = false;

    for (let element = 0; element < length; element++)
    {
      if (array[element] > array[element + 1])
      {
        let temp = array[element];
        array[element] = array[element + 1];
        array[element + 1] = temp;
        swapped = true;
      }
    }
  } while (swapped);

  return array;
}

