function insertionSort (array)
{
  let length = array.length;

  for (let element = 1; element < length; element++)
  {
    let value = array[element];
    let previous = element - 1;

    while (previous >= 0 && array[previous] > value)
    {
      array[previous + 1] = array[previous];

      previous = previous - 1;
    }
    array[previous + 1] = value;
  }

  return array;
}

array = [4, 1, 3, 2, 5];
insertionSort(array);

