function selectionSort (array)
{
  let length = array.length;

  for (let element = 0; element < length; element++)
  {
    let minimum = element;

    for (let neighbor = element + 1; neighbor < length; neighbor++)
    {
      if (array[minimum] > array[neighbor])
      {
        minimum = neighbor;
      }
    }
    if (minimum !== element)
    {
      let temp = array[element];
      array[element] = array[minimum];
      array[minimum] = temp;
    }
  }

  return array;
}
// Input: 1 4 3 2 5
array = [1, 4, 3, 2, 5];
selectionSort(array);

