/**
 * @param {string} s
 * @param {string[]} wordDict
 * @return {string[]}
 */
var wordBreak = function(s, wordDict) {
  var dp = Array(s.length);
  var map = {};
  var res = [];

  for (var i = 0; i < wordDict.length; i++) {
    map[wordDict[i]] = true;
  }

  return find(s, map, dp, 0);
};

var find = function (s, map, dp, index) {
  if (dp[index]) return dp[index];

  var str = '';
  var tmp = null;
  var len = s.length;

  dp[index] = [];

  for (var i = index; i < len; i++) {
    str = s.substring(index, i + 1);
    if (!map[str]) continue;
    if (i === len - 1) {
      dp[index].push(str);
      break;
    }
    tmp = find(s, map, dp, i + 1);
    for (var j = 0; j < tmp.length; j++) {
      dp[index].push(str + ' ' + tmp[j]);
    }
  }

  return dp[index];
};



// dp[i] represents the way that s.substring(i) is composed of words in wordDict.
// Time complexity : O(n^2)
// Space complexity : O(n^2)

